import React, { useRef, useState } from "react";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faStarHalfStroke,
  faArrowRight,
  faArrowLeft,
  faLocationDot,
  faStar as filledStar,
  faChevronLeft,
  faClock,
  faPhone,
  faGlobe,
  faChevronDown,
  faChevronUp,
} from "@fortawesome/free-solid-svg-icons";
import { faStar as emptyStar } from "@fortawesome/free-regular-svg-icons";

import img1 from "./img/jpg1.jpg";
import img2 from "./img/jpg2.jpg";
import img3 from "./img/jpg3.jpg";
export default function SpotDetail({ placeDetails, lessDetails }) {
  const data = {
    placeId: placeId,
  };

  const searchFilters = document.querySelector(".search-filters");
  if (searchFilters) {
    searchFilters.style.marginTop = "0dvh";
  }

  fetch("/api/search", {
    method: "POST",
    headers: {
      "Content-Type": "application/json",
    },
    body: JSON.stringify(data),
  })
    .then((response) => response.json())
    .then((result) => {
      console.log("Success:", result.googleData.results);
      setSearchResults(result.googleData.results);
    })
    .catch((error) => {
      console.error("Error:", error);
    });

  const starRating = (rating) => {
    const stars = [];

