import React, { useEffect, useRef } from "react";
import { useLoadScript } from "@react-google-maps/api";
const google = window.google;
const maps = window.maps;

const { AdvancedMarkerElement } = await google.importLibrary("marker");

const libraries = ["places", "geometry"]; // Add other libraries if needed

export default function LocationsMap() {
  const center = { lat: 37.42, lng: -122.1 };
  const zoom = 14;

  const markersData = [
    {
      position: { lat: 37.42, lng: -122.1 },
      price: "$2.5M",
    },
  ];

  const { isLoaded } = useLoadScript({
    googleMapsApiKey: process.env.React_App_Api_Key, // Note: ensure environment variable is correct
    libraries,
  });

  const mapRef = useRef(null);

  useEffect(() => {
    if (isLoaded && mapRef.current) {
      const { Map, AdvancedMarkerElement } = window.google.maps;

      const map = new Map(mapRef.current, {
        center,
        zoom,
        mapId: "4504f8b37365c3d0",
      });

      markersData.forEach((data) => {
        const priceTag = document.createElement("div");
        priceTag.className = "price-tag";
        priceTag.textContent = data.price;

        new AdvancedMarkerElement({
          map,
          position: data.position,
          content: priceTag,
        });
      });
    }
  }, [isLoaded, zoom]);

  return <div ref={mapRef} style={{ height: "100vh", width: "100%" }} />;
}
