import React, { useRef, useState } from "react";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faStarHalfStroke,
  faArrowRight,
  faArrowLeft,
  faLocationDot,
} from "@fortawesome/free-solid-svg-icons";
import { faStar as emptyStar } from "@fortawesome/free-regular-svg-icons";
import {
  faStar as filledStar,
  faChevronLeft,
  faClock,
  faPhone,
  faGlobe,
} from "@fortawesome/free-solid-svg-icons";

import img1 from "./img/jpg1.jpg";
import img2 from "./img/jpg2.jpg";
import img3 from "./img/jpg3.jpg";
export default function SpotDetail({ placeDetails }) {
  const starRating = (rating) => {
    const stars = [];

    for (let i = 1; i <= 5; i++) {
      if (rating >= i) {
        stars.push(<FontAwesomeIcon key={i} icon={filledStar} />);
      } else if (rating >= i - 0.5) {
        stars.push(<FontAwesomeIcon key={i} icon={faStarHalfStroke} />);
      } else {
        stars.push(<FontAwesomeIcon key={i} icon={emptyStar} />);
      }
    }

    return stars;
  };

  const [imgMargin, setImgMargin] = useState(0);

  const nextImg = (direction) => {
    if (direction === "right") {
      if (imgMargin > 100) return;
      setImgMargin((imgMargin) => {
        return imgMargin + 100;
      });
    }
    if (direction === "left") {
      if (imgMargin === 0) return;
      setImgMargin((imgMargin) => {
        return imgMargin - 100;
      });
    }
  };

  return (
    <div className="spot-detail">
      <div className="spot-detail-wrapper">
        <div className="spot-detail-carousel">
          <div
            className="spot-detail-img"
            style={{
              backgroundImage: `url(${img1})`,
              marginLeft: `-${imgMargin}%`,
            }}
          ></div>
          <div
            className="spot-detail-img"
            style={{
              backgroundImage: `url(${img2})`,
              marginLeft: `-${imgMargin}%`,
            }}
          ></div>
          <div
            className="spot-detail-img"
            style={{
              backgroundImage: `url(${img3})`,
              marginLeft: `-${imgMargin}%`,
            }}
          ></div>
        </div>
        <div className="spot-detail-img-btn spot-detail-btn">
          <FontAwesomeIcon icon={faChevronLeft} />
        </div>
        <div className="spot-detail-controlls">
          <div
            className={`spot-detail-btn ${imgMargin === 0 ? "disabled" : ""}`}
            onClick={() => nextImg("left")}
          >
            <FontAwesomeIcon icon={faArrowLeft} />
          </div>
          <div
            className={`spot-detail-btn ${imgMargin > 100 ? "disabled" : ""}`}
            onClick={() => nextImg("right")}
          >
            <FontAwesomeIcon icon={faArrowRight} />
          </div>
        </div>
      </div>
      <div className="spot-detail-container">
        <h2 className="spot-detail-name">Domowe Obiadki</h2>
        <div>4.4 {starRating(4.4)} (230)</div>
        <div>
          <b className="map-result-open">Open</b> ⋅ Closes 12 AM
        </div>

        <div className="spot-detail-info">
          <div>
            <FontAwesomeIcon icon={faLocationDot} />
            <p>Fabryczna 18, Pietrzykowice</p>
          </div>
          <div>
            <FontAwesomeIcon icon={faClock} /> <p>Open ⋅ Opens 11 AM Mon</p>
          </div>
          <div>
            <FontAwesomeIcon icon={faGlobe} /> <p>facebook.com</p>
          </div>
          <div>
            <FontAwesomeIcon icon={faPhone} /> <p>+48 661 180 596</p>
          </div>
        </div>

        <h2>Reviews</h2>
        <div>
          <div>Photo</div>
          <div>Christian Arm</div>
          <div>
            <FontAwesomeIcon icon={filledStar} />
            <FontAwesomeIcon icon={filledStar} />
            <FontAwesomeIcon icon={filledStar} />
            <FontAwesomeIcon icon={filledStar} />
            <FontAwesomeIcon icon={filledStar} />
          </div>
          <div>5 years ago</div>
          <div>
            Always worth a stop! It tastes like it used to be at home. In the
            Former German Democratic Republic!
          </div>
        </div>

        <div className="spot-detail-footer">
          <div>View in google maps</div>
          <div>Add to favourites</div>
        </div>
      </div>
    </div>
  );
}
