import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { SearchContext } from "./SearchContext"; // Import the context

export default function SearchFilters() {
  const { selectedSubcategory, location } = useContext(SearchContext); // Destructure the context to get the selected subcategory

  const handleSubmit = async (e) => {
    const url = "https://maps.googleapis.com/maps/api/place/nearbysearch/json";
    const params = {
      keyword: selectedSubcategory,
      location: location,
      radius: document.getElementById("distance").value,
      key: process.env.React_App_Api_Key,
    };

    console.log(location);
  };

  return (
    <div className="search-filters">
      <div className="filter-header">
        <Link to="/">
          <i className="fa-solid fa-angle-left"></i>
        </Link>
        <p>Clear Filters</p>
      </div>
      <h2>Filters</h2>
      <div className="category">
        <p>Category</p>
        <Link to="category">
          <div className="category-box">
            <p>{selectedSubcategory}</p>{" "}
            <i className="fa-solid fa-angle-right"></i>
          </div>
        </Link>
      </div>

      <div className="category">
        <p>Location</p>
        <Link to="">
          <div className="category-box">
            {/* To do: add the option to pick the location from the map */}
            <p>Current Location</p>
            <i className="fa-solid fa-angle-right"></i>
          </div>
        </Link>
      </div>

      <div className="category">
        <p>Distance</p>
        <input type="number" id="distance" />
      </div>

      <button onClick={handleSubmit}>Search Spots</button>
    </div>
  );
}
