import React, { useState, useEffect, useContext } from "react";
import { APIProvider, Map, Marker } from "@vis.gl/react-google-maps";
import { darkThemeStyles, blueBallIcon } from "./mapStyles.js";
import { SearchContext } from "./SearchContext";

export default function LocationsMap() {
  const { userLat, setUserLat, userLng, setUserLng, results, setResults } =
    useContext(SearchContext);

  const [mapMarkers, setMapMarkers] = useState([]);
  const mapRef = useRef(null);

  useEffect(() => {
    const getLocation = () => {
      if (navigator.geolocation) {
        navigator.geolocation.getCurrentPosition(
          (position) => {
            setUserLat(position.coords.latitude);
            setUserLng(position.coords.longitude);
          },
          (error) => {
            console.error("Error getting location:", error);
            setFallbackLocation();
          }
        );
      } else {
        setFallbackLocation();
      }
    };

    const setFallbackLocation = () => {
      setUserLat(51.1027203);
      setUserLng(16.9439538);
    };

    getLocation();
  }, [setUserLat, setUserLng]);

  useEffect(() => {
    if (results && mapRef.current) {
      const newMarkers = results.map((result, index) => {
        const markerElement = document.createElement("div");
        markerElement.classList.add("custom-marker");
        markerElement.innerHTML = `<div class="marker-content">${result.name}</div>`;

        const marker = new window.google.maps.marker.AdvancedMarkerView({
          map: mapRef.current,
          position: result.geometry.location,
          content: markerElement,
        });

        return marker;
      });

      setMapMarkers(newMarkers);

      return () => {
        // Clean up markers when component unmounts or results change
        newMarkers.forEach((marker) => marker.setMap(null));
      };
    }
  }, [results]);

  useEffect(() => {
    if (userLat !== null && userLng !== null && mapRef.current) {
      const userMarkerElement = document.createElement("div");
      userMarkerElement.classList.add("custom-user-marker");
      userMarkerElement.innerHTML = `<img src="${blueBallIcon}" alt="User Location" />`;

      const userMarker = new window.google.maps.marker.AdvancedMarkerView({
        map: mapRef.current,
        position: { lat: userLat, lng: userLng },
        content: userMarkerElement,
      });

      return () => {
        // Clean up user marker on unmount
        userMarker.setMap(null);
      };
    }
  }, [userLat, userLng]);

  if (userLat === null || userLng === null) {
    return <div>Loading map...</div>;
  }

  return (
    <APIProvider apiKey={process.env.React_App_Api_Key}>
      <Map
        id="map"
        onLoad={(map) => (mapRef.current = map)}
        keyboardShortcuts={false}
        disableDefaultUI={true}
        defaultZoom={13}
        defaultCenter={{ lat: userLat, lng: userLng }}
        options={{
          styles: darkThemeStyles,
        }}
      >
        {/* The advanced markers will be automatically added through the useEffect hooks */}
      </Map>
    </APIProvider>
  );
}
