import React, { useEffect, useRef } from "react";
import { APIProvider, Map } from "@vis.gl/react-google-maps";
import darkThemeStyles from "./mapStyles.js";

export default function LocationsMap() {
  const mapRef = useRef(null);
  const markersRef = useRef([]);

  useEffect(() => {
    if (mapRef.current) {
      const { map } = mapRef.current;

      // Example locations to place AdvancedMarkers
      const locations = [
        { lat: 51.0443583, lng: 16.8675189 },
        { lat: 51.05, lng: 16.87 },
        // Add more locations as needed
      ];

      locations.forEach((location) => {
        // Create an AdvancedMarker and add it to the markersRef
        const marker = new window.google.maps.marker.AdvancedMarkerElement({
          position: location,
          map: map,
          title: "My Advanced Marker", // Example additional option
        });

        // Store the marker in the markersRef array
        markersRef.current.push(marker);
      });

      // Example of how to interact with markers later
      markersRef.current.forEach((marker, index) => {
        // Adding a click listener to each marker
        marker.addListener("click", () => {
          console.log(`Marker ${index + 1} clicked!`);
        });
      });
    }
  }, []);

  return (
    <APIProvider apiKey={"AIzaSyBLzOyErw_GGeOYghEGKdDdV8Wyfx7kTpw"}>
      <Map
        ref={mapRef} // Attach a ref to the Map component
        id="map"
        keyboardShortcuts={false}
        disableDefaultUI={true}
        defaultZoom={13}
        defaultCenter={{ lat: 51.0443583, lng: 16.8675189 }}
        options={{
          styles: darkThemeStyles, // Apply dark mode styles
        }}
        onLoad={(map) => {
          // Save the map instance to the ref
          mapRef.current = { map };
        }}
      />
    </APIProvider>
  );
}
