import React from "react";

export default function Categories() {
  const handleClick1 = () => {
    const mainCategories = document.querySelector(".main-categories");
    mainCategories.style.zIndex = "0";
  };

  const handleClick2 = () => {
    const mainCategories = document.querySelector(".subcategories");
    mainCategories.style.zIndex = "3";
  };

  return (
    <div className="main-categories">
      <i class="fa-solid fa-angle-left" onClick={handleClick1}></i>
      <h2>Choose Category</h2>
      <div className="main-category" onClick={handleClick2}>
        <p>Food</p>
        <i class="fa-solid fa-angle-right"></i>
      </div>
      <div className="main-category">
        <p>Sport</p>
        <i class="fa-solid fa-angle-right"></i>
      </div>
      <div className="main-category">
        <p>Services</p>
        <i class="fa-solid fa-angle-right"></i>
      </div>
      <div className="main-category">
        <p>Entertaiment</p>
        <i class="fa-solid fa-angle-right"></i>
      </div>
      <div className="main-category">
        <p>Health and Wellness</p>
        <i class="fa-solid fa-angle-right"></i>
      </div>
      <div className="main-category">
        <p>Lodging</p>
        <i class="fa-solid fa-angle-right"></i>
      </div>
      <div className="main-category">
        <p>Shopping</p>
        <i class="fa-solid fa-angle-right"></i>
      </div>
    </div>
  );
}
