import React from "react";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faXmark, faStarHalfStroke } from "@fortawesome/free-solid-svg-icons";
import { faStar as emptyStar } from "@fortawesome/free-regular-svg-icons";
import { faStar as filledStar } from "@fortawesome/free-solid-svg-icons";

import img1 from "./img/jpg3.jpg";

export default function SpotDetail({ placeDetails }) {
  console.log(placeDetails);
  const starRating = (rating) => {
    const stars = [];

    for (let i = 1; i <= 5; i++) {
      if (rating >= i) {
        stars.push(<FontAwesomeIcon key={i} icon={filledStar} />);
      } else if (rating >= i - 0.5) {
        stars.push(<FontAwesomeIcon key={i} icon={faStarHalfStroke} />);
      } else {
        stars.push(<FontAwesomeIcon key={i} icon={emptyStar} />);
      }
    }

    return stars;
  };

  return (
    <div className="spot-detail">
      {/* <img className="spot-detail-img" src={img1} alt="Images 1" /> */}
      <div
        className="spot-detail-img"
        style={{ backgroundImage: `url(${img1})` }}
      >
        {" "}
      </div>
      <div>
        <h3>Domowe Obiadki</h3>
      </div>
      <div>
        4.4
        <FontAwesomeIcon icon={filledStar} />
        <FontAwesomeIcon icon={filledStar} />
        <FontAwesomeIcon icon={filledStar} />
        <FontAwesomeIcon icon={filledStar} />
        <FontAwesomeIcon icon={filledStar} />
      </div>
      <div>Open ⋅ Closes 12 AM</div>

      <div>Fabryczna 18, Pietrzykowice</div>
      <div>Open ⋅ Opens 11 AM Mon</div>
      <div>facebook.com</div>
      <div>+48 661 180 596</div>

      <h2>Reviews</h2>
      <div>
        <div>Photo</div>
        <div>Christian Arm</div>
        <div>
          <FontAwesomeIcon icon={filledStar} />
          <FontAwesomeIcon icon={filledStar} />
          <FontAwesomeIcon icon={filledStar} />
          <FontAwesomeIcon icon={filledStar} />
          <FontAwesomeIcon icon={filledStar} />
        </div>
        <div>5 years ago</div>
        <div>
          Always worth a stop! It tastes like it used to be at home. In the
          Former German Democratic Republic!
        </div>
      </div>

      <div className="spot-detail-footer">
        <div>View in google maps</div>
        <div>Add to favourites</div>
      </div>
    </div>
  );
}
