import React, { useContext, useState } from "react";
import { Link } from "react-router-dom";
// import { SearchContext } from "./SearchContext";
// import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleLeft } from "@fortawesome/free-solid-svg-icons";

import usePlacesAutocomplete, {
  getGeocode,
  getLatLng,
} from "use-places-autocomplete";

import {
  Combobox,
  ComboboxInput,
  ComboboxPopover,
  ComboboxList,
  ComboboxOption,
  // ComboboxOptionText,
} from "@reach/combobox";
import "@reach/combobox/styles.css";

const slideValue = document.querySelector("span");
const inputSlider = document.querySelector("input");
inputSlider.oninput = () => {
  let value = inputSlider.value;
  slideValue.textContent = value;
  slideValue.style.left = value / 2 + "%";
  slideValue.classList.add("show");
};
inputSlider.onblur = () => {
  slideValue.classList.remove("show");
};

export default function SearchFilters({ setLocation }) {
  const {
    ready,
    value,
    setValue,
    suggestions: { status, data },
    clearSuggestions,
  } = usePlacesAutocomplete();

  const handleSelect = async (val) => {
    setValue(val, false);
    clearSuggestions();

    const results = await getGeocode({ address: val });
    const { lat, lng } = await getLatLng(results[0]);
    setLocation({ lat, lng });
  };

  const handleClick = () => {
    const searchFilters = document.querySelector(".search-filters");
    if (searchFilters) {
      searchFilters.style.marginTop = "0dvh";
    }
  };
  return (
    <div className="search-filters">
      <div className="filter-header">
        <FontAwesomeIcon
          icon={faAngleLeft}
          className="icon"
          onClick={handleClick}
        />
        <p>Clear Filters</p>
      </div>

      <div className="category">
        <p>Location</p>
        <Combobox onSelect={handleSelect} className="test">
          <ComboboxInput
            className="category-box"
            value={value}
            onChange={(e) => setValue(e.target.value)}
            placeholder="Search an adress"
          />
          <ComboboxPopover>
            <ComboboxList>
              {status === "OK" &&
                data.map(({ place_id, description }) => (
                  <ComboboxOption key={place_id} value={description} />
                ))}
            </ComboboxList>
          </ComboboxPopover>
        </Combobox>
      </div>

      <div className="range">
        <div className="sliderValue">
          <span>100</span>
        </div>
        <div className="field">
          <div className="value left">0</div>
          <input type="range" min="0" max="200" value="100" steps="1" />
          <div className="value right">200</div>
        </div>
      </div>
    </div>
  );
}
