import React from "react";
import { APIProvider, Map, Marker } from "@vis.gl/react-google-maps";

import darkThemeStyles from "./mapStyles.js";

// Define custom marker content as a blue ball
const BlueBallMarker = () => (
  <div
    style={{
      width: "20px", // Width of the ball
      height: "20px", // Height of the ball
      borderRadius: "50%", // Make the div round
      backgroundColor: "blue", // Blue color
      border: "2px solid white", // Optional: white border for better visibility
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
    }}
  >
    {/* You can add text or icons inside the ball if needed */}
  </div>
);

export default function LocationsMap() {
  return (
    <APIProvider apiKey={"AIzaSyBLzOyErw_GGeOYghEGKdDdV8Wyfx7kTpw"}>
      <Map
        id="map"
        keyboardShortcuts={false}
        disableDefaultUI={true}
        defaultZoom={13}
        defaultCenter={{ lat: 51.0443583, lng: 16.8675189 }}
        options={{
          styles: darkThemeStyles, // Apply dark mode styles
        }}
      >
        {/* Example of using Marker with a custom blue ball */}
        <Marker
          position={{ lat: 51.0443583, lng: 16.8675189 }}
          icon={{
            url:
              "data:image/svg+xml;base64," +
              btoa(`
              <svg width="24" height="24" xmlns="http://www.w3.org/2000/svg">
                <circle cx="12" cy="12" r="10" stroke="white" stroke-width="2" fill="blue" />
              </svg>
            `),
          }}
        />
      </Map>
    </APIProvider>
  );
}
