import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";
// import { SearchContext } from "./SearchContext";
// import axios from "axios";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faAngleLeft } from "@fortawesome/free-solid-svg-icons";

import usePlacesAutocomplete, {
  getGeocode,
  getLatLng,
} from "use-places-autocomplete";

import {
  Combobox,
  ComboboxInput,
  ComboboxPopover,
  ComboboxList,
  ComboboxOption,
  // ComboboxOptionText,
} from "@reach/combobox";
import "@reach/combobox/styles.css";

export default function SearchFilters({ setLocation }) {
  const {
    ready,
    value,
    setValue,
    suggestions: { status, data },
    clearSuggestions,
  } = usePlacesAutocomplete();

  const handleSelect = async (val) => {
    setValue(val, false);
    clearSuggestions();

    const results = await getGeocode({ address: val });
    const { lat, lng } = await getLatLng(results[0]);
    setLocation({ lat, lng });
  };

  const handleClick = () => {
    const searchFilters = document.querySelector(".search-filters");
    if (searchFilters) {
      searchFilters.style.marginTop = "0dvh";
    }
  };

  const slider = document.getElementById("myRange");
  const output = document.getElementById("value");

  output.innerHTML = slider.value;

  slider.oninput = function () {
    output.innerHTML = this.value;
  };

  const [sliderValue, setSliderValue] = useState(1); // Initial value of the slider
  const [sliderBackground, setSliderBackground] = useState("");

  useEffect(() => {
    const x = sliderValue;
    const color = `linear-gradient(90deg, rgb(117, 252, 117) ${x}%, rgb(214, 214, 214) ${x}%)`;
    setSliderBackground(color);
  }, [sliderValue]); // Update the background gradient when sliderValue changes

  const handleSliderChange = (e) => {
    setSliderValue(e.target.value);
  };

  return (
    <div className="search-filters">
      <div className="filter-header">
        <FontAwesomeIcon
          icon={faAngleLeft}
          className="icon"
          onClick={handleClick}
        />
        <p>Clear Filters</p>
      </div>

      <div className="category">
        <p>Location</p>
        <Combobox onSelect={handleSelect} className="test">
          <ComboboxInput
            className="category-box"
            value={value}
            onChange={(e) => setValue(e.target.value)}
            placeholder="Search an adress"
          />
          <ComboboxPopover>
            <ComboboxList>
              {status === "OK" &&
                data.map(({ place_id, description }) => (
                  <ComboboxOption key={place_id} value={description} />
                ))}
            </ComboboxList>
          </ComboboxPopover>
        </Combobox>
      </div>

      <div className="slideContainer">
        <p>
          Value: <span id="value"></span>
        </p>
        <input
          type="range"
          min="1"
          max="100"
          value={sliderValue}
          id="myRange"
          className="slider"
          onChange={handleSliderChange}
          style={{ background: sliderBackground }}
        />
      </div>
    </div>
  );
}
