import { useState, useMemo, useCallback, useRef, useEffect } from "react";
// import { Circle } from "@react-google-maps/api";

import {
  APIProvider,
  Map,
  useMap,
  Marker,
  AdvancedMarker,
} from "@vis.gl/react-google-maps";

import { MarkerClusterer } from "@googlemaps/markerclusterer";

import SearchFilters from "./SearchFilters";
import Spots from "./Spots";
import SearchBar from "./SearchBar";
import { blueBallIcon } from "./mapStyles.js";

export default function GoogleMap() {
  const [location, setLocation] = useState("");
  const [sliderValue, setSliderValue] = useState(15);
  const [spotValue, setSpotValue] = useState("Restaurant");
  const [searchResults, setSearchResults] = useState([]);
  const mapRef = useRef();
  const center = useMemo(() => ({ lat: 51.103574, lng: 16.943842 }), []);
  const options = useMemo(
    () => ({
      mapId: "b181cac70f27f5e6",
      disableDefaultUI: true,
      clickableIcons: false,
    }),
    []
  );
  const onLoad = useCallback((map) => (mapRef.current = map), []);

  const handleSearch = () => {
    const data = {
      keyword: spotValue,
      location: {
        lat: location.lat,
        lng: location.lng,
      },
      radius: sliderValue,
    };

    const searchFilters = document.querySelector(".search-filters");
    if (searchFilters) {
      searchFilters.style.marginTop = "0dvh";
    }

    fetch("/api/search", {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    })
      .then((response) => response.json())
      .then((result) => {
        console.log("Success:", result.googleData.results);
        setSearchResults(result.googleData.results);
      })
      .catch((error) => {
        console.error("Error:", error);
      });
  };

  const map = useMap();
  const [markers, setMarkers] = useState({});
  const clusterer = useRef(null);

  useEffect(() => {
    if (!map) return;
    if (!clusterer.current) {
      clusterer.current = new MarkerClusterer({ map });
    }
  }, [map]);

  useEffect(() => {
    clusterer.current?.clearMarkers();
    clusterer.current?.addMarkers(Object.values(markers));
  }, [markers]);

  const setMarkerRef = (marker, key) => {
    if (marker && markers[key]) return;
    if (!marker && !markers[key]) return;

    setMarkers((prev) => {
      if (marker) {
        return { ...prev, [key]: marker };
      } else {
        const newMarkers = { ...prev };
        delete newMarkers[key];
        return newMarkers;
      }
    });
  };

  return (
    <>
      <div className="container">
        <SearchBar />
        <div className="map">
          <APIProvider apiKey={process.env.React_App_Api_Key}>
            <Map
              defaultZoom={10}
              defaultCenter={center}
              options={options}
              mapContainerClassName="map-container2"
              onLoad={onLoad}
            >
              {location && (
                <>
                  {/* <Circle center={location} radius={sliderValue * 100} /> */}
                  <Marker
                    position={location}
                    icon={{
                      url: blueBallIcon,
                    }}
                  />
                </>
              )}

              {searchResults &&
                searchResults.map((result) => (
                  <AdvancedMarker
                    key={result.place_id}
                    position={result.geometry.location}
                    ref={(marker) => setMarkerRef(marker, result.place_id)}
                  >
                    <span>🔥</span>
                  </AdvancedMarker>
                ))}
            </Map>
          </APIProvider>
        </div>
        <Spots />
      </div>
      <SearchFilters
        setLocation={(position) => {
          setLocation(position);
          mapRef.current?.panTo(position);
        }}
        sliderValue={sliderValue}
        setSliderValue={setSliderValue}
        spotValue={spotValue}
        setSpotValue={setSpotValue}
        handleSearch={handleSearch}
      />
    </>
  );
}
