import React from "react";
import { APIProvider, Map, Marker } from "@vis.gl/react-google-maps";
import darkThemeStyles from "./mapStyles.js";

export default function LocationsMap() {
  const getLocation = () => {
    const [userLat, setUserLat] = useState(null);
    const [userLng, setUserLng] = useState(null);

    useEffect(() => {
      const getLocation = () => {
        if (navigator.geolocation) {
          navigator.geolocation.getCurrentPosition(
            (position) => {
              setUserLat(position.coords.latitude);
              setUserLng(position.coords.longitude);
            },
            (error) => {
              console.error("Error getting location:", error);
              setFallbackLocation();
            }
          );
        } else {
          setFallbackLocation();
        }
      };

      const setFallbackLocation = () => {
        setUserLat(51.1027203);
        setUserLng(16.9439538);
      };

      getLocation();
    }, []);
  };
  const blueBallSVG = `
    <svg width="24" height="24" xmlns="http://www.w3.org/2000/svg">
      <circle cx="8" cy="8" r="7" stroke="white" stroke-width="2" fill="blue" />
    </svg>
  `;

  const blueBallIcon = "data:image/svg+xml;base64," + btoa(blueBallSVG);

  return (
    <APIProvider apiKey={"AIzaSyBLzOyErw_GGeOYghEGKdDdV8Wyfx7kTpw"}>
      <Map
        id="map"
        keyboardShortcuts={false}
        disableDefaultUI={true}
        defaultZoom={13}
        defaultCenter={{ lat: userLat, lng: userLng }}
        options={{
          styles: darkThemeStyles,
        }}
      >
        <Marker
          position={{ lat: userLat, lng: userLng }}
          icon={{
            url: blueBallIcon,
          }}
        />
      </Map>
    </APIProvider>
  );
}
