import React, { useState } from "react";

export default function SearchBar() {
  let [menuOpen, setMenuOpen] = useState(false);

  console.log(menuOpen);

  const handleClick1 = () => {
    const options = document.querySelector(".options");
    options.style.marginTop = "0px";
    setMenuOpen(true);
  };

  const handleClick2 = () => {
    const closeMenu = document.querySelector(".map-search-icon");
    closeMenu.style.marginTop = "100vh";
    setMenuOpen(false);
  };

  return (
    <div className="map-search" onClick={handleClick1}>
      <div className="map-search-icon" onClick={handleClick2}>
        {menuOpen ? (
          <i className="fa-solid fa-arrow-left"></i>
        ) : (
          <i className="fa-solid fa-location-dot"></i>
        )}
      </div>
      <div className="map-search-bar">
        <input
          className="map-search-bar-input"
          placeholder="Search here"
        ></input>
      </div>
      <div className="map-search-bar-glass">
        <i class="fa-solid fa-magnifying-glass"></i>
      </div>
    </div>
  );
}
