import React, { useRef, useState } from "react";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faStarHalfStroke,
  faArrowRight,
  faArrowLeft,
  faLocationDot,
} from "@fortawesome/free-solid-svg-icons";
import { faStar as emptyStar } from "@fortawesome/free-regular-svg-icons";
import {
  faStar as filledStar,
  faChevronLeft,
  faClock,
  faPhone,
  faGlobe,
} from "@fortawesome/free-solid-svg-icons";

import img1 from "./img/jpg1.jpg";
import img2 from "./img/jpg2.jpg";
import img3 from "./img/jpg3.jpg";
export default function SpotDetail({ placeDetails }) {
  const starRating = (rating) => {
    const stars = [];

    for (let i = 1; i <= 5; i++) {
      if (rating >= i) {
        stars.push(<FontAwesomeIcon key={i} icon={filledStar} />);
      } else if (rating >= i - 0.5) {
        stars.push(<FontAwesomeIcon key={i} icon={faStarHalfStroke} />);
      } else {
        stars.push(<FontAwesomeIcon key={i} icon={emptyStar} />);
      }
    }

    return stars;
  };

  const [imgMargin, setImgMargin] = useState(0);

  const nextImg = (direction) => {
    if (direction === "right") {
      if (imgMargin > 100) return;
      setImgMargin((imgMargin) => {
        return imgMargin + 100;
      });
    }
    if (direction === "left") {
      if (imgMargin === 0) return;
      setImgMargin((imgMargin) => {
        return imgMargin - 100;
      });
    }
  };

  const periods = [
    {
      close: { date: "2024-09-23", day: 1, time: "1500" },
      open: { date: "2024-09-23", day: 1, time: "1100" },
    },
    {
      close: { date: "2024-09-17", day: 2, time: "1500" },
      open: { date: "2024-09-17", day: 2, time: "1100" },
    },
    {
      close: { date: "2024-09-18", day: 3, time: "1500" },
      open: { date: "2024-09-18", day: 3, time: "1100" },
    },
    {
      close: { date: "2024-09-19", day: 4, time: "1500" },
      open: { date: "2024-09-19", day: 4, time: "1100" },
    },
    {
      close: { date: "2024-09-20", day: 5, time: "1500" },
      open: { date: "2024-09-20", day: 5, time: "1100" },
    },
  ];

  // Function to display the opening or closing hours
  function displayHours() {
    const now = new Date();
    const currentDay = now.getDay(); // Sunday is 0, Monday is 1, etc.
    const currentTime = now.getHours() * 100 + now.getMinutes(); // Convert current time to 24-hour format as a number, e.g., 1430 for 2:30 PM

    // Find today's hours in the periods array
    const todayPeriod = periods.find(
      (period) => period.open.day === currentDay
    );

    if (!todayPeriod) {
      console.log("Closed today.");
      return;
    }

    const openTime = parseInt(todayPeriod.open.time, 10);
    const closeTime = parseInt(todayPeriod.close.time, 10);

    if (currentTime >= openTime && currentTime <= closeTime) {
      console.log(`Currently open. Closes at: ${formatTime(closeTime)}`);
    } else if (currentTime < openTime) {
      console.log(`Currently closed. Opens at: ${formatTime(openTime)}`);
    } else {
      console.log("Closed for today.");
    }
  }

  // Helper function to format time from 24-hour number to readable string (e.g., 1500 to 3:00 PM)
  function formatTime(time) {
    const hours = Math.floor(time / 100);
    const minutes = time % 100;
    const ampm = hours >= 12 ? "PM" : "AM";
    const formattedHours = hours % 12 || 12; // Convert to 12-hour format
    const formattedMinutes = minutes.toString().padStart(2, "0");
    return `${formattedHours}:${formattedMinutes} ${ampm}`;
  }

  displayHours();

  return (
    <div className="spot-detail">
      <div className="spot-detail-wrapper">
        <div className="spot-detail-carousel">
          <div
            className="spot-detail-img"
            style={{
              backgroundImage: `url(${img1})`,
              marginLeft: `-${imgMargin}%`,
            }}
          ></div>
